mapper.insert:

	SELECT last_insert_id();

INSERT INTO customer(
	customer_id,
	customer_pw
)VALUES(
	#{customerId},
	PASSWORD(#{customerPw})
)

mapper.update:
UPDATE customer SET
	customer_active = 'N'
WHERE customer_no = #{customerNo}

UPDATE customer_detail SET
	customer_membership_end = #{customerMembershipEnd}
WHERE customer_no = #{customerNo}

mapper.select:
SELECT COUNT(customer_no)
FROM customer_img
WHERE customer_no = #{customerNo}
